targets.expr:
avg by (node, aggr) (
    aggr_disk_busy{aggr=~"$Aggregate",netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}
  )
and
  topk(
    $TopResources,
    avg by (node, aggr) (
      avg_over_time(
        aggr_disk_busy{aggr=~"$Aggregate",netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}[3h]
      )
    )
  )

avg by (node, aggr) (
    aggr_disk_busy{aggr=~"$Aggregate",netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}
  )
and
  topk(
    $TopResources,
    avg by (node, aggr) (
      avg_over_time(
        aggr_disk_busy{aggr=~"$Aggregate",netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}[3h]
      )
    )
  )

volume_avg_latency{aggr=~".*$Aggregate.*",netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}
and on (datacenter, netapp_cluster, svm, volume)
  topk(
    $TopResources,
    avg_over_time(
      volume_avg_latency{aggr=~".*$Aggregate.*",netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}[3h] @ end()
    )
  )

(
