sum by (switch, interface, mac) (
    rate(
      cisco_interface_transmit_bytes{datacenter=~"$Datacenter",interface=~"$Interface",switch=~"$Switch"}[4m]
    )
  )
and on (switch, interface, mac)
  topk(
    $TopResources,
    sum by (switch, interface, mac) (
      avg_over_time(
        cisco_interface_transmit_bytes{datacenter=~"$Datacenter",interface=~"$Interface",switch=~"$Switch"}[3h]
      )
    )
  )

