targets.expr:
(
        100
      *
        sum by (datacenter, cluster, svm) (
          volume_size_used{cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM"}
        )
    / on (datacenter, cluster, svm)
      sum by (datacenter, cluster, svm) (
        volume_size_total{cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM"}
      )
  )
and on (datacenter, cluster, svm)
  topk(
    $TopResources,
      (
            100
          *
            sum by (datacenter, cluster, svm) (
              avg_over_time(
                volume_size_used{cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM"}[3h] @ end()
              )
            )
        / on (datacenter, cluster, svm)
          sum by (datacenter, cluster, svm) (
            avg_over_time(
              volume_size_total{cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM"}[3h] @ end()
            )
          )
      )
    * on (datacenter, cluster, svm) group_left ()
      svm_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",root_svm=~"No|$IncludeRoot",svm=~"$SVM"}
  )

