targets.expr:
sum by (node) (
  (
    node_total_data{datacenter=~"$Datacenter", cluster=~"$Cluster"}
  )
  and
  topk(
    5,
    (
      avg_over_time(
        node_total_data{datacenter=~"$Datacenter", cluster=~"$Cluster", node!=""}[3h] @ end()
      )
    )
  )
)

sum by (node) (
  (
    node_total_data{datacenter=~"$Datacenter", cluster=~"$Cluster"}
  )
  and
  topk(
    5,
    (
      avg_over_time(
        node_total_data{datacenter=~"$Datacenter", cluster=~"$Cluster", node!=""}[3h] @ end()
      )
    )
  )
)

(
    svm_vol_read_data{datacenter=~"$Datacenter", cluster=~"$Cluster"} 
    +
    svm_vol_write_data{datacenter=~"$Datacenter", cluster=~"$Cluster"}
)
and on(datacenter,cluster, svm)
topk(
  $TopResources,
  (
    avg_over_time(svm_vol_read_data{datacenter=~"$Datacenter", cluster=~"$Cluster"}[3h])
    + avg_over_time(svm_vol_write_data{datacenter=~"$Datacenter", cluster=~"$Cluster"}[3h])
  )
)
