targets.expr:
sum(
  avg_over_time(
    environment_sensor_power{datacenter=~"$Datacenter", cluster=~"$Cluster"}[1h]
  )
) + sum(
  avg_over_time(
    shelf_power{datacenter=~"$Datacenter", cluster=~"$Cluster"}[1h]
  ) * on(datacenter, cluster, shelf) shelf_labels{datacenter=~"$Datacenter", cluster=~"$Cluster", isEmbedded="No"}
)

(
    sum by (datacenter,cluster, aggr) (aggr_disk_user_reads{datacenter=~"$Datacenter", cluster=~"$Cluster"})
)
and on(datacenter,cluster, aggr)
topk(
    $TopResources,
    sum by (datacenter,cluster, aggr) (
        avg_over_time(aggr_disk_user_reads{datacenter=~"$Datacenter", cluster=~"$Cluster"}[3h])
    )
)

(
    sum by (datacenter,cluster, aggr) (aggr_disk_user_writes{datacenter=~"$Datacenter", cluster=~"$Cluster"})
)
and on(datacenter,cluster, aggr)
topk(
    $TopResources,
    sum by (datacenter,cluster, aggr) (
        avg_over_time(aggr_disk_user_writes{datacenter=~"$Datacenter", cluster=~"$Cluster"}[3h])
    )
)
