targets.expr:
count(
    group by (datacenter, cluster) (
                                support_labels{asup_enabled="true",asup_https_configured!="https",cluster=~"$Cluster",datacenter=~"$Datacenter"}
                              or
                                security_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",telnet_enabled="true"}
                            or
                              security_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",rsh_enabled="true"}
                          or
                              group by (datacenter, cluster) (
                                security_account_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",hash_algorithm="md5"}
                              )
                            >
                              0
                        or
                          cluster_peer_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",encryption_state=""}
                      or
                        cluster_peer_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",encryption_state="none"}
                    or
                        group by (datacenter, cluster) (
                          security_account_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",locked="false"}
                        )
                      >
                        0
                  or
                    security_login_labels{banner="",cluster=~"$Cluster",datacenter=~"$Datacenter",scope="cluster"}
                or
                  security_login_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$Cluster"}
              or
                count by (datacenter, cluster) (ntpserver_labels{cluster=~"$Cluster",datacenter=~"$Datacenter"}) < 1
            or
              security_audit_destination_status{cluster=~"$Cluster",datacenter=~"$Datacenter",protocol!="tcp_encrypted"}
          or
            security_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",fips_enabled="false"}
        or
          svm_labels{ciphers=~".*_cbc.*",cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM",type="admin"}
      or
        group by (datacenter, cluster) (
                (
                    svm_labels{cifs_protocol_enabled="true",cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM"}
                  or
                      svm_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",nfs_protocol_enabled="true",svm=~"$SVM"}
                    and
                      svm_labels{audit_protocol_enabled="false",cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM"}
                )
              or
                security_ssh_labels{ciphers=~".*_cbc.*",cluster=~"$Cluster",datacenter=~"$Datacenter"}
            or
              security_login_labels{banner="",cluster=~"$Cluster",datacenter=~"$Datacenter",scope="svm"}
          or
            security_login_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM"}
        )
    )
  )
or
  vector(0)

