targets.expr:
avg(
  topk(
    $TopResources,
      (
        avg by (datacenter, cluster, svm, volume) (
          volume_avg_latency{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
        )
      )
    * on (datacenter, cluster, svm, volume) group_left (node)
      (
        max by (datacenter, cluster, svm, volume, node) (
          volume_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",junction_path=~"$Junction",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
        )
      )
  )
)

sum(
    topk(
      $TopResources,
        (
          sum by (datacenter, cluster, svm, volume) (
            volume_read_data{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
          )
        )
      * on (datacenter, cluster, svm, volume) group_left (node)
        (
          max by (datacenter, cluster, svm, volume, node) (
            volume_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",junction_path=~"$Junction",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
          )
        )
    )
  )
+
  sum(
    topk(
      $TopResources,
        (
          sum by (datacenter, cluster, svm, volume) (
            volume_write_data{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
          )
        )
      * on (datacenter, cluster, svm, volume) group_left (node)
        (
          max by (datacenter, cluster, svm, volume, node) (
            volume_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",junction_path=~"$Junction",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
          )
        )
    )
  )

sum(
  topk(
    $TopResources,
      (
        sum by (datacenter, cluster, svm, volume) (
          volume_total_ops{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
        )
      )
    * on (datacenter, cluster, svm, volume) group_left (node)
      (
        max by (datacenter, cluster, svm, volume, node) (
          volume_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",junction_path=~"$Junction",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
        )
      )
  )
)

volume_avg_latency{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
and on (datacenter, cluster, svm, volume)
  topk(
    $TopResources,
    (
      max by (datacenter, cluster, svm, volume) (
          avg_over_time(
            volume_avg_latency{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}[3h] @ end()
          )
        * on (datacenter, cluster, svm, volume) group_left ()
          (
            max by (datacenter, cluster, svm, volume) (
              volume_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",junction_path=~"$Junction",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
            )
          )
      )
    )
  )

(
      volume_read_data{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
    +
      volume_write_data{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
  )
and on (datacenter, cluster, svm, volume)
  topk(
    $TopResources,
    (
        max by (datacenter, cluster, svm, volume) (
            avg_over_time(
              volume_read_data{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}[3h] @ end()
            )
          * on (datacenter, cluster, svm, volume) group_left ()
            (
              max by (datacenter, cluster, svm, volume) (
                volume_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",junction_path=~"$Junction",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
              )
            )
        )
      +
        max by (datacenter, cluster, svm, volume) (
            avg_over_time(
              volume_write_data{cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}[3h] @ end()
            )
          * on (datacenter, cluster, svm, volume) group_left ()
            (
              max by (datacenter, cluster, svm, volume) (
                volume_labels{cluster=~"$Cluster",datacenter=~"$Datacenter",junction_path=~"$Junction",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
              )
            )
        )
    )
  )

