targets.expr:
eseries_controller_used_cache_memory{array=~"$Array",controller=~"$Controller",datacenter=~"$Datacenter"}
and on (datacenter, array, controller, controller_id)
  topk(
    $TopResources,
    max by (datacenter, array, controller, controller_id) (
      avg_over_time(
        eseries_controller_used_cache_memory{array=~"$Array",controller=~"$Controller",datacenter=~"$Datacenter"}[3h] @ end()
      )
    )
  )

