sum by (cluster, datacenter, node) (
    rate(
      storagegrid_s3_operations_successful{cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}[4m]
    )
  )
and
  topk(
    $TopResources,
    sum by (cluster, datacenter, node) (
      avg_over_time(
        storagegrid_s3_operations_successful{cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}[3h]
