targets.expr:
max by (aggr, node) (
    aggr_disk_max_total_transfers{aggr=~"$Aggregate",cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}
  )
and
  topk(
    $TopResources,
    max by (aggr, node) (
      avg_over_time(
        aggr_disk_max_total_transfers{aggr=~"$Aggregate",cluster=~"$Cluster",datacenter=~"$Datacenter",node=~"$Node"}[3h] @ end()
      )
    )
  )

