targets.expr:
(sum(environment_sensor_power{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"}) or vector(0))
+
  (
      sum(
          shelf_power{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"}
        * on (datacenter, netapp_cluster, shelf)
          shelf_labels{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",isEmbedded="No"}
      )
    or
      vector(0)
  )

count(
    (
        volume_labels{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",snapshot_policy=~"none.*",volume!~"MDV.*"}
      * on (snapshot_policy) group_left ()
        group by (snapshot_policy) (snapshot_policy_labels)
    )
  )
or on ()
  vector(0)

sum by (netapp_cluster) (
    aggr_logical_used_wo_snapshots_flexclones{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"}
  )
and
  topk(
    $TopResources,
    sum by (netapp_cluster) (
      avg_over_time(
        aggr_logical_used_wo_snapshots_flexclones{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"}[3h] @ end()
      )
    )
  )

sum by (netapp_cluster) (
    aggr_physical_used_wo_snapshots_flexclones{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"}
  )
and
  topk(
    $TopResources,
    sum by (netapp_cluster) (
      avg_over_time(
        aggr_physical_used_wo_snapshots_flexclones{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"}[3h] @ end()
      )
    )
  )

sum by (netapp_cluster) (aggr_logical_used_wo_snapshots{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"})
and
  topk(
    $TopResources,
    sum by (netapp_cluster) (
      avg_over_time(
        aggr_logical_used_wo_snapshots{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"}[3h] @ end()
      )
    )
  )

sum by (netapp_cluster) (aggr_physical_used_wo_snapshots{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"})
and
  topk(
    $TopResources,
    sum by (netapp_cluster) (
      avg_over_time(
        aggr_physical_used_wo_snapshots{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter"}[3h] @ end()
      )
    )
  )
