targets.expr:
avg(
  topk(
    $TopResources,
      (
        avg by (datacenter, netapp_cluster, svm, volume) (
          volume_avg_latency{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
        )
      )
    * on (datacenter, netapp_cluster, svm, volume) group_left (node)
      (
        max by (datacenter, netapp_cluster, svm, volume, node) (
          volume_labels{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
        )
      )
  )
)

sum(
    topk(
      $TopResources,
        (
          sum by (datacenter, netapp_cluster, svm, volume) (
            volume_read_data{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
          )
        )
      * on (datacenter, netapp_cluster, svm, volume) group_left (node)
        (
          max by (datacenter, netapp_cluster, svm, volume, node) (
            volume_labels{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
          )
        )
    )
  )
+
  sum(
    topk(
      $TopResources,
        (
          sum by (datacenter, netapp_cluster, svm, volume) (
            volume_write_data{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",style!="flexgroup_constituent",svm=~"$SVM",volume=~"$Volume"}
          )
        )
      * on (datacenter, netapp_cluster, svm, volume) group_left (node)
        (
          max by (datacenter, netapp_cluster, svm, volume, node) (
            volume_labels{netapp_cluster=~"$Cluster",datacenter=~"$Datacenter",svm=~"$SVM",tags=~".*$Tag.*",volume=~"$Volume"}
          )
        )
    )
  )

sum(
